//Draw to OLED
bool oled_task_user() {
 // Set cursor position
 oled_set_cursor(0, 0);

if (is_keyboard_left()) {
 // Write current layer to OLED display
switch (get_highest_layer(layer_state)) {
 case _QWERTY :
 render_main();
  break;
 case _SYMBOL :
 render_symbol();
  break;
 case _MOUSE :
 render_mouse();
  break;
  
#ifdef JOYSTICK_ENABLE
 case _GAMES :
 render_games();
  break;
#endif

#ifdef STENO_ENABLE
 case _STENO :
 render_steno();
  break;
#endif

 }
} else {
 render_logo();
}
return false;
}
